async function onMessageReactionAdd(reaction, user) {
  if (user.bot) return;

  if (reaction.partial) {
    try {
      await reaction.fetch();
    } catch (error) {
      console.error('Failed to fetch reaction: ', error);
      return;
    }
  }

  const emoji = reaction.emoji.name;
  const message = reaction.message;

  // Only allow in specific channel
  if (message.channel.id !== CONCORDIUM_CHANNEL) return;

  // Only allow users with specific role
  const member = await message.guild.members.fetch(user.id);
  if (!member.roles.cache.has(NURTURED_BY_CONCORDIUM_ROLE)) return;

  const offeringResponse = offeringResponses[emoji];
  if (!offeringResponse) {
    message.channel.send(`Pathetic. That is not an acceptable offering. -XP <@${user.id}>`);
    updateXP(user.id, -1);
    return;
  }

  if (typeof offeringResponse === 'function') {
    offeringResponse(message, user);
    return;
  }
}

module.exports = {
    onMessageReactionAdd,
}
